import React, {useContext} from 'react';
import SamplerGrid from "../SamplerGrid/SamplerGrid";
import EditorItemRange from '../PadEditorItem/EditorItemRange';
import {Context} from '../../contexts/SamplerContext';
import {handlePadTrigger} from '../../actions'
import Colors from '../../Config/ColorScheme';
import ColorSelector from '../ColorSelector/ColorSelector';
import touchCTRL from '../../Config/touchControls';
import './Pad.css';
import { setCTX } from "../../actions";

export default (props) => {
    const context = useContext(Context);
    let currentPad = context.gridPadsArr[props.id]
    let selectedPad = context.gridPadsArr[context.selectedPad];
    let max = 0;
    let source = context.gridPadsArr[context.selectedPad].source;
    let borderColor = currentPad.source ? currentPad.color : Colors.gray;
    let color = props.id === context.selectedPad ? currentPad.color : Colors.black;
    const handleTouchStart = (padId) => {
        if(!touchCTRL[padId].hold){
            touchCTRL[padId].hold = true;
            handlePadTrigger(context, padId);
        }
    }
    const handleTouchEnd = (padId) => {
        touchCTRL[padId].hold = false;
    }
    const handleMouseClick = (padId) => {
        if(!context.touchEnabled){
            handlePadTrigger(context, padId)
        }
    }
    return (
      <React.Fragment>

    <div
    className="pad"
    id={props.id}
    style={{
        backgroundColor: color,
        color: Colors.white,
        transition: "0.25s linear",
    }}
    onClick={() => {handleMouseClick(props.id)}}
    onDoubleClick={(e) => e.preventDefault()}
    onTouchStart={(e) => {handleTouchStart(props.id, e)}}
    onTouchEnd={(e) => {handleTouchEnd(props.id, e)}}
    >
    <span className="pad-text">{props.midiNote}</span>
    </div>

    <div className="editor-wrapper">

        <EditorItemRange label="start" value={selectedPad.sampleStart} max={max} step={0.001} />
        <EditorItemRange label="end" value={selectedPad.sampleEnd} max={max} step={0.001} />
        <EditorItemRange label="gain" value={context.gridPadsArr[context.selectedPad].currentGain} max={1} step={0.01} />
        <EditorItemRange label="detune" value={context.gridPadsArr[context.selectedPad].detune} step={25} min={0} max={1000} />
        <ColorSelector />
        <EditorItemRange label="placeholder3" value={0} />
    </div>
)


    </React.Fragment>
  );
}
