import React, { useState, useContext } from "react";
import { Context } from "../../contexts/SamplerContext";
import { handlePadTrigger } from "../../actions";
import keyCTRL from "../../Config/keyboardControls";
import StartScreen from "../StartScreen/StartScreen";
import SamplerGrid from "../SamplerGrid/SamplerGrid";
import "./App.css";

export default function App() {
  const [showModal, setShowModal] = useState(false);
  const [musicPlayerHidden, setMusicPlayerHidden] = useState("music-player-hidden");
  const [musicPlayer01, setMusicPlayer01] = useState("music-player01");
  const [musicPlayer02, setMusicPlayer02] = useState("music-player02");
  const [musicPlayer03, setMusicPlayer03] = useState("music-player03")
  const [musicPlayer04, setMusicPlayer04] = useState("music-player04");
  const [musicPlayer05, setMusicPlayer05] = useState("music-player05")
  const [musicPlayer06, setMusicPlayer06] = useState("music-player06");
  const [musicPlayer07, setMusicPlayer07] = useState("music-player07")
  const [musicPlayer08, setMusicPlayer08] = useState("music-player08")
  const [musicPlayer09, setMusicPlayer09] = useState("music-player09");
  const [musicPlayer10, setMusicPlayer10] = useState("music-player10")

  const context = useContext(Context);
  const renderAppContent = () => {
    if (!context.ctx) return <StartScreen />;
    return (
      <div className="app-wrapper">
        <SamplerGrid />
      </div>
    );
  };

  const handleKeyDown = e => {
    e.preventDefault();
    e.stopPropagation();
    let keyTrigger = keyCTRL[e.which];
    if (keyTrigger && !keyTrigger.hold && !e.repeat) {
      keyCTRL[e.which].hold = true;
      handlePadTrigger(context, keyTrigger.padId);
    }
  };

  const handleKeyUp = e => {
    e.preventDefault();
    e.stopPropagation();
    let keyTrigger = keyCTRL[e.which];
    if (keyTrigger && keyTrigger.hold) {
      keyCTRL[e.which].hold = false;
    }
  };

  const showHideComponents = e => {
    if (showModal === false) {
      setShowModal(true);
    } else if (showModal === true) {
      setShowModal(false);
    }
  };

  const swapMPlayerLeft = e => {
    if (musicPlayer01 === "music-player01") {
      setMusicPlayer01("music-player02");
    }
    if (musicPlayer02 === "music-player02") {
      setMusicPlayer02("music-player03");
    }
    if (musicPlayer01 === "music-player02") {
      setMusicPlayer01("music-player03");
    }
  };

  const swapMPlayerRight = e => {
    if (musicPlayer01 === "music-player01") {
      setMusicPlayer01("music-player04");
    }
    if (musicPlayer02 === "music-player02") {
      setMusicPlayer02("music-player01");
    }
    if (musicPlayer02 === "music-player01") {
      setMusicPlayer02("music-player04");
    }
    if (musicPlayer03 === "music-player03") {
      setMusicPlayer03("music-player02");
    }
    if (musicPlayer03 === "music-player02") {
      setMusicPlayer03("music-player01");
    }
    if (musicPlayer03 === "music-player01") {
      setMusicPlayer03("music-player04");
    }
    if (musicPlayer04 === "music-player04") {
      setMusicPlayer04("music-player03");
    }
    if (musicPlayer04 === "music-player03") {
      setMusicPlayer04("music-player02");
    }
    if (musicPlayer04 === "music-player02") {
      setMusicPlayer04("music-player01");
    }
    if (musicPlayer05 === "music-player05") {
      setMusicPlayer05("music-player04");
    }
    if (musicPlayer05 === "music-player04") {
      setMusicPlayer05("music-player03");
    }
    if (musicPlayer05 === "music-player03") {
      setMusicPlayer05("music-player02");
    }
    if (musicPlayer05 === "music-player02") {
      setMusicPlayer05("music-player01");
    }
    if (musicPlayer05 === "music-player01") {
      setMusicPlayer05("music-player04");
    }

  };

  return (
    <React.Fragment>
      <div id="body-wrapper">
        <header></header>

        <div className="logo-container">
          <h1 className="logo">ALGERNON CORNELIUS</h1>
        </div>
        <div className="box"></div>
        <div className="box02"></div>
        <div onClick={swapMPlayerLeft} className="x2">
          x
        </div>
        <div onClick={swapMPlayerRight} className="x3">
          x
        </div>

        <div className="video-wrapper">
          <iframe
            className="video"
            src="https://www.youtube.com/embed/vFNbj8KI0Rc?start=3&controls=0&showinfo=0&loop=1&modestbranding=1"
            frameborder="0"
            allow="accelerometer; autoplay; encrypted-media; gyroscope; picture-in-picture;"
          ></iframe>
        </div>

        <iframe className={musicPlayer01} src="https://bandcamp.com/EmbeddedPlayer/track=97838974/size=large/bgcol=333333/linkcol=ffffff/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/track/as-long-as-theres-you">As Long As There&#39;s You by Algernon Cornelius</a></iframe>

        <iframe className={musicPlayer02} src="https://bandcamp.com/EmbeddedPlayer/track=2424298729/size=large/bgcol=ffffff/linkcol=0687f5/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/track/adieu">Adieu by Algernon Cornelius</a></iframe>

<iframe className={musicPlayer03} src="https://bandcamp.com/EmbeddedPlayer/track=2431711501/size=large/bgcol=333333/linkcol=ffffff/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/track/radio-edit">Radio Edit by Algernon Cornelius</a></iframe>

<iframe className={musicPlayer04} src="https://bandcamp.com/EmbeddedPlayer/track=4044113520/size=large/bgcol=333333/linkcol=ffffff/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/track/absolute-badman-riddim">Absolute Badman Riddim by Algernon Cornelius</a></iframe>

<iframe className={musicPlayer05} src="https://bandcamp.com/EmbeddedPlayer/album=1535006646/size=large/bgcol=ffffff/linkcol=0687f5/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/album/who-knows-maybe-they-speak-for-you">Who Knows? Maybe They Speak For You by Algernon Cornelius</a></iframe>

<iframe className={musicPlayer06} src="https://bandcamp.com/EmbeddedPlayer/album=1122987924/size=large/bgcol=ffffff/linkcol=0687f5/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/album/exit-the-wu-tang-36-chambers-ac-version">Exit the Wu-Tang: 36 Chambers (AC Version) by Algernon Cornelius</a></iframe>

<iframe className={musicPlayer07} src="https://bandcamp.com/EmbeddedPlayer/album=3420241321/size=large/bgcol=333333/linkcol=ffffff/tracklist=false/transparent=true/" seamless><a href="http://algernoncornelius.bandcamp.com/album/algernoncornelius-dark-beats-only-quit-yer-sen">AlgernonCornelius - dARK bEATS oNLY / Quit Yer Sen - ^ by Algernon Cornelius</a></iframe>


        {showModal && (
          <div
            className="mpc-wrapper"
            onKeyDown={e => {
              handleKeyDown(e);
            }}
            onKeyUp={e => {
              handleKeyUp(e);
            }}
            tabIndex="0"
          >
            {renderAppContent()}
          </div>
        )}
      </div>
    </React.Fragment>
  );
}
